/* ==UserStyle==
@name         Claude - Rose of Dune
@namespace    github.com/yherrero/stylus
@version      2.0.0
@description  A soft rose-gold theme for Claude.ai with warm brown accents (Base16)
@author       yherrero
@license      MIT
==/UserStyle== */
@-moz-document domain("claude.ai") {
  :root,
  :root[data-mode="dark"],
  :root[data-mode="light"],
  [data-theme="claude"][data-mode="light"],
  [data-theme="claude"][data-mode="dark"] {
    --accent-brand: #b76e79 !important;
    --accent-main-000: #b76e79 !important;
    --accent-main-100: #b76e79 !important;
    --accent-main-200: #af5d69 !important;
    --accent-main-900: #c89098 !important;
    --accent-pro-000: #AD9980 !important;
    --accent-pro-100: #AD9980 !important;
    --accent-pro-200: #a38d71 !important;
    --accent-pro-900: #c1b29f !important;
    --accent-secondary-000: #B36E5B !important;
    --accent-secondary-100: #B36E5B !important;
    --accent-secondary-200: #a7614d !important;
    --accent-secondary-900: #c38d7e !important;
    --bg-000: #EBD1B8 !important;
    --bg-100: #F5E6D3 !important;
    --bg-200: #F5E6D3 !important;
    --bg-300: #EBD1B8 !important;
    --bg-400: #EBD1B8 !important;
    --bg-500: #e8c9ac !important;
    --border-000: #F5E6D3 !important;
    --border-100: rgba(107, 88, 67, 0.15) !important;
    --border-200: rgba(107, 88, 67, 0.2) !important;
    --border-300: rgba(107, 88, 67, 0.2) !important;
    --border-400: #AD9980 !important;
    --border-500: #AD9980 !important;
    --danger-000: #ca6054 !important;
    --danger-100: #B34438 !important;
    --danger-200: #a03d32 !important;
    --danger-900: rgba(179, 68, 56, 0.2) !important;
    --oncolor-000: #EBD1B8 !important;
    --oncolor-100: #EBD1B8 !important;
    --oncolor-200: #F5E6D3 !important;
    --oncolor-300: #E6D1C2 !important;
    --text-000: #3C2B20 !important;
    --text-100: #3C2B20 !important;
    --text-200: #3C2B20 !important;
    --text-300: #6B5843 !important;
    --text-400: #7A6A57 !important;
    --text-500: #7A6A57 !important;
    --ring-color: #b76e79 !important;
    --focus-ring-color: rgba(183, 110, 121, 0.5) !important;
  }
  .bg-bg-100,
  .bg-bg-200 {
    background-color: #F5E6D3 !important;
  }
  .bg-bg-000,
  .bg-bg-300 {
    background-color: #EBD1B8 !important;
  }
  #__next,
  [data-testid="app-root"],
  .flex.min-h-screen,
  .min-h-screen {
    background-color: #F5E6D3 !important;
  }
  body {
    background: #F5E6D3 !important;
    color: #3C2B20 !important;
  }
  nav {
    background: #EBD1B8 !important;
  }
  nav.\!bg-bg-200,
  .bg-bg-200 {
    background: #EBD1B8 !important;
  }
  main {
    background: #F5E6D3 !important;
  }
  .code-block__code {
    background: #EBD1B8 !important;
  }
  .code-block__code code span {
    color: #3C2B20 !important;
  }
  .code-block__code code span.token[style*="color: rgb(152, 195, 121)"],
  .code-block__code code span.token.string {
    color: #B88642 !important;
  }
  .code-block__code code span.token[style*="color: rgb(209, 154, 102)"],
  .code-block__code code span.token.keyword,
  .code-block__code code span.token.attr-name {
    color: #B36E5B !important;
  }
  .code-block__code code span.token[style*="color: rgb(97, 175, 239)"],
  .code-block__code code span.token.function,
  .code-block__code code span.token.tag {
    color: #C87C6A !important;
  }
  .code-block__code code span.token[style*="color: rgb(224, 108, 117)"],
  .code-block__code code span.token.important,
  .code-block__code code span.token.deleted {
    color: #B34438 !important;
  }
  .code-block__code code span.token[style*="color: rgb(198, 120, 221)"],
  .code-block__code code span.token.class-name,
  .code-block__code code span.token.builtin {
    color: #D28C74 !important;
  }
  .code-block__code code span.token[style*="color: rgb(171, 178, 191)"],
  .code-block__code code span.token.comment {
    color: #7A6A57 !important;
  }
  .code-block__code code span.token.number {
    color: #D9A06B !important;
  }
  .code-block__code code span.token.operator {
    color: #6B5843 !important;
  }
  .code-block__code code span.token.punctuation {
    color: #7A6A57 !important;
  }
  .code-block__code code span.token.regex {
    color: #7CA973 !important;
  }
  code:not(pre code),
  code.text-danger-000 {
    background: #EBD1B8 !important;
    color: #B34438 !important;
    padding: 0.125rem 0.375rem;
    border-radius: 4px;
  }
  div.bg-black\/80 {
    background: #EBD1B8 !important;
  }
  div.text-white {
    color: #3C2B20 !important;
  }
  div.text-\[\#D97757\] {
    color: #b76e79 !important;
  }
  div.text-always-white svg {
    color: #F5E6D3 !important;
  }
  button.bg-accent-pro-100 > span.inline-block {
    color: #F5E6D3 !important;
  }
  button.bg-accent-secondary-900 span.text-text-000 {
    color: #F5E6D3 !important;
  }
  button.bg-accent-secondary-900 span.text-text-300 {
    color: #EBD1B8 !important;
  }
  button.bg-accent-secondary-900 div.bg-accent-secondary-000 {
    background: #965745 !important;
  }
  div.\!text-danger-000.\[\&\[data-highlighted\]\]\:bg-danger-900[data-highlighted] {
    background: rgba(179, 68, 56, 0.2) !important;
  }
  input[type="checkbox"] + div.bg-bg-000 {
    background: #3C2B20 !important;
  }
  a {
    color: #C87C6A !important;
  }
  a:hover {
    color: #ba5b45 !important;
  }
  input,
  textarea {
    background: #E6D1C2 !important;
    border-color: rgba(107, 88, 67, 0.2) !important;
    color: #3C2B20 !important;
  }
  input::placeholder,
  textarea::placeholder {
    color: #7A6A57 !important;
  }
  input:focus,
  textarea:focus {
    border-color: #b76e79 !important;
    box-shadow: 0 0 0 2px rgba(183, 110, 121, 0.5) !important;
  }
  .ProseMirror {
    background: #EBD1B8 !important;
    color: #3C2B20 !important;
  }
  .ProseMirror::placeholder,
  .ProseMirror .placeholder {
    color: #7A6A57 !important;
  }
  [data-testid="human-turn"] {
    background: #EBD1B8 !important;
  }
  [data-testid="assistant-turn"] {
    background: #F5E6D3 !important;
  }
  .hover\:bg-bg-300:hover {
    background: rgba(107, 88, 67, 0.1) !important;
  }
  .bg-bg-300 {
    background: rgba(183, 110, 121, 0.2) !important;
  }
  *::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }
  *::-webkit-scrollbar-track {
    background: #EBD1B8;
  }
  *::-webkit-scrollbar-thumb {
    background: rgba(107, 88, 67, 0.4);
    border-radius: 4px;
  }
  *::-webkit-scrollbar-thumb:hover {
    background: rgba(107, 88, 67, 0.6);
  }
  [role="dialog"] {
    background: #F5E6D3 !important;
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  [role="menu"],
  [role="listbox"] {
    background: #E6D1C2 !important;
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  [role="menuitem"]:hover,
  [role="option"]:hover {
    background: rgba(107, 88, 67, 0.1) !important;
  }
  .artifact-container,
  [data-testid="artifact"] {
    background: #E6D1C2 !important;
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  table {
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  table th {
    background: #EBD1B8 !important;
    color: #3C2B20 !important;
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  table td {
    border-color: rgba(107, 88, 67, 0.15) !important;
  }
  table tr:hover {
    background: rgba(107, 88, 67, 0.1) !important;
  }
  blockquote {
    border-left-color: #AD9980 !important;
    background: rgba(173, 153, 128, 0.08) !important;
    color: #6B5843 !important;
  }
  hr {
    border-color: rgba(107, 88, 67, 0.2) !important;
  }
  ul,
  ol {
    color: #3C2B20 !important;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: #2B1E15 !important;
  }
  strong,
  b {
    color: #3C2B20 !important;
  }
  em,
  i {
    color: #6B5843 !important;
  }
  ::selection {
    background-color: rgba(183, 110, 121, 0.6) !important;
  }
}
